import React, {useState} from 'react';
import '../header.css';
import logo from '../images/logo.png';import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAnglesDown } from '@fortawesome/free-solid-svg-icons'; // Import icons



const Header = function() {
    const [isVisible, setIsVisible] = useState({
        navVisibilty: false,
        dropdownVisible: false
    });

    const toggleVisibility = (key) => {
        setIsVisible((prevState) => ({
            ...prevState, 
            [key]: !prevState[key],
        }));
      };

    return( 
        <header className='header'>
            <div className='header-logo'>
                <img alt='logo' src={logo} />
                <h1 className= 'header-tagline'>Digital Lab Assistant</h1>
            </div>
            <nav className={`header-nav ${isVisible.navVisibilty ? 'visible' : ''}`}>
                <ul>
                <li><a href='#'>Home</a></li>
                <li><a href='#'>Contact Us</a></li>
                </ul>
            </nav>
            {/* <div className="header-collapse" onClick={() => toggleVisibility('dropdownVisible')}>
                <button>
                    <FontAwesomeIcon icon={faAnglesDown} bounce style={{ color: "#283618" }} />
                </button> */}
            <div className="header-collapse" onClick={() => toggleVisibility('navVisible')}>
                <FontAwesomeIcon icon={faAnglesDown} bounce style={{ color: "#283618" }} />
                </div>
      <div className="header-dropdown" onClick={() => toggleVisibility('dropdownVisible')}>
        <FontAwesomeIcon icon={faAnglesDown} bounce style={{ color: "#283618" }} />
                    {isVisible.dropdownVisible && (
                        <ul className="dropdown-menu">
                            <li><a href="#">Home</a></li>
                            <li><a href="#">Contact Us</a></li>
                        </ul>
                     )}
            </div>
        </header>
    );
}

export default Header;
